DROP TABLE IF EXISTS `sw_class`;
CREATE TABLE `sw_class` (
  `c_id` smallint(6) NOT NULL AUTO_INCREMENT,
  `c_name` varchar(20) NOT NULL COMMENT '课程名称',
  `c_pid` smallint(6) NOT NULL DEFAULT '0',
  `c_desc` text COMMENT '课程介绍',
  `c_content` text,
  `c_level` tinyint(4) NOT NULL DEFAULT '0',
  `c_pic` varchar(255) NOT NULL DEFAULT '' COMMENT '课程图片',
  `c_order_by` tinyint(4) NOT NULL DEFAULT '50',
  `c_date` int(10) NOT NULL DEFAULT '0',
  `c_is_index` tinyint(4) NOT NULL DEFAULT '0' COMMENT '首页',
  `c_is_hot` tinyint(4) NOT NULL DEFAULT '0' COMMENT '推荐',
  `c_is_top` tinyint(4) NOT NULL DEFAULT '0' COMMENT '置顶',
  `c_author` varchar(30) NOT NULL DEFAULT '' COMMENT '作者',
  `c_count` int(10) NOT NULL DEFAULT '0' COMMENT '点击率',
  PRIMARY KEY (`c_id`)
) ENGINE=InnoDB AUTO_INCREMENT=70 DEFAULT CHARSET=utf8;

