INSERT INTO `sw_class` VALUES ('59', '初步了解Linux', '20', '不管是在日常生活还是在工作中，Linux都是必不可少的，本次课程带你初步了解常用指令以及操作', '&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;&lt;strong&gt;常用指令&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;ls　　 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;显示文件或目录&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;-l &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 列出文件详细信息l(list)&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;-a &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;列出当前目录下所有文件及目录，包括隐藏的a(all)&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;mkdir &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 创建目录&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;-p &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 创建目录，若无父目录，则创建p(parent)&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;cd &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 切换目录&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;touch &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;创建空文件&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;echo &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;创建带有内容的文件。&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;cat &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;查看文件内容&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;cp &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;拷贝&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;mv &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 移动或重命名&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;rm &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 删除文件&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;-r &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;递归删除，可删除子目录及文件&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;-f &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;强制删除&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;find &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;在文件系统中搜索某文件&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;wc &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;统计文本中行数、字数、字符数&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;grep &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 在文本文件中查找某个字符串&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;rmdir &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 删除空目录&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;tree &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 树形结构显示目录，需要安装tree包&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;pwd &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;显示当前目录&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;ln &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;创建链接文件&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;more、less &amp;nbsp;分页显示文本文件内容&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;head、tail &amp;nbsp; &amp;nbsp;显示文件头、尾内容&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;ctrl+alt+F1 &amp;nbsp;命令行全屏模式&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;&lt;strong&gt;&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;系统管理命令&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;stat &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;显示指定文件的详细信息，比ls更详细&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;who &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 显示在线登陆用户&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;whoami &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;显示当前操作用户&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;hostname &amp;nbsp; &amp;nbsp; &amp;nbsp;显示主机名&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;uname &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 显示系统信息&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;top &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;动态显示当前耗费资源最多进程信息&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;ps &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;显示瞬间进程状态 ps -aux&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;du &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;查看目录大小 du -h /home带有单位显示目录信息&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-size:21px;&quot;&gt;&lt;span style=&quot;font-family:\'comic sans ms\', sans-serif;&quot;&gt;df &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;查看磁盘大小 df -h 带有单位显示磁盘信息&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:15px;font-family:&amp;quot;&quot;&gt;\r\n	&lt;a name=&quot;systemManager&quot;&gt;&lt;/a