-- ----------------------------
-- Records of sw_news
-- ----------------------------
INSERT INTO `sw_news` VALUES ('3', '大型网站技术架构-入门梳理', '罗列了大型网站架构涉及到的概念，附上了简单说明', '&lt;h2 style=&quot;font-weight:normal;font-size:30px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n	前言\r\n&lt;/h2&gt;\r\n&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n	&lt;li&gt;\r\n		本文是对《大型网站架构设计》(李智慧 著)一书的梳理，类似文字版的“思维导图”\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		全文主要围绕“性能，可用性，伸缩性，扩展性，安全”这五个要素\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		性能，可用性，伸缩性这几个要素基本都涉及到应用服务器，缓存服务器，存储服务器这几个方面\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;h2 style=&quot;font-weight:normal;font-size:30px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n	概述\r\n&lt;/h2&gt;\r\n&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n	&lt;li&gt;\r\n		三个纬度：演化、模式、要素\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		五个要素： 性能，可用性，伸缩性，扩展性，安全\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;h3 style=&quot;font-weight:normal;font-size:26px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n	演化历程\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n	图例可参考&amp;nbsp;&lt;a href=&quot;http://www.hollischuang.com/archives/728&quot; target=&quot;_blank&quot;&gt;大型网站架构演化历程&lt;/a&gt;：\r\n&lt;/p&gt;\r\n&lt;ol style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;初始阶段的网站架构&lt;/strong&gt;：一台服务器，上面同时拥有应用程序，数据库，文件，等所有资源。例如 LAMP 架构\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;应用和数据服务分离&lt;/strong&gt;：三台服务器（硬件资源各不相同），分别是应用服务器，文件服务器和数据库服务器\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;使用缓存改善网站性能&lt;/strong&gt;：分为两种，缓存在应用服务器上的本地缓存和缓存在专门的分布式缓存服务器的远程缓存\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;使用应用服务器集群改善网站并发处理能力&lt;/strong&gt;：通过负载均衡调度服务器来将访问请求分发到应用服务器集群中的任何一台机器\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;数据库读写分离&lt;/strong&gt;：数据库采用主从热备，应用服务器在写数据时访问主数据库，主数据库通过主从复制机制将数据更新同步到从数据库。应用服务器使用专门的数据访问模块从而对应用透明\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;使用反向代理和 CDN 加速网站响应&lt;/strong&gt;：这两者基本原理都是缓存。反向代理部署在网站的中心机房，CDN 部署在网络提供商的机房\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;使用分布式文件系统和分布式数据库系统&lt;/strong&gt;：数据库拆分的最后手段，更常用的是业务分库\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;使用 NoSQL 和搜索引擎&lt;/strong&gt;：对可伸缩的分布式有更好的支持\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;业务拆分&lt;/strong&gt;：将整个网站业务拆分成不同的应用，每个应用独立部署维护，应用之间通过超链接建立联系/消息队列进行数据分发/访问同一数据存储系统\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;分布式服务&lt;/strong&gt;：公共业务提取出来独立部署&lt;br /&gt;\r\n&lt;img src=&quot;/Public/js/editor/attached/image/20170121/20170121214826_65288.png&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			演化的价值观\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				大型网站架构的核心价值是随网站所需灵活应对\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				驱动大型网站技术发展的主要力量是网站的业务发展\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			误区\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				一味追随大公司的解决方案\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				为了技术而技术\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				企图用技术解决所有问题\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h3 style=&quot;font-weight:normal;font-size:26px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n			架构模式\r\n		&lt;/h3&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			模式的关键在于模式的可重复性\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;分层&lt;/strong&gt;：横向切分\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;分割&lt;/strong&gt;：纵向切分\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;分布式&lt;/strong&gt;：分层和分割的主要目的是为了切分后的模块便于分布式部署。常用方案：\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						分布式应用和服务\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						分布式静态资源\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						分布式数据和存储\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						分布式计算\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						分布式配置，分布式锁，分布式文件，等等\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;集群&lt;/strong&gt;：多台服务器部署相同的应用构成一个集群，通过负载均衡设备共同对外提供服务\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;缓存&lt;/strong&gt;：将数据放距离计算最近的位置加快处理速度，改善性能第一手段，可以加快访问速度，减小后端负载压力。使用缓存&amp;nbsp;&lt;strong&gt;两个前提条件&lt;/strong&gt;&amp;nbsp;：1.数据访问热点不均衡；2.数据某时段内有效，不会很快过期\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						CDN\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						反向代理\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						本地缓存\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						分布式缓存\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;异步&lt;/strong&gt;：旨在系统解耦。异步架构是典型的消费者生产者模式，特性如下：\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						提高系统可用性\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						加快网站访问速度\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						消除并发访问高峰\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;冗余&lt;/strong&gt;：实现高可用。数据库的冷备份和热备份\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;自动化&lt;/strong&gt;：包括发布过程自动化，自动化代码管理，自动化测试，自动化安全检测，自动化部署，自动化监控，自动化报警，自动化失效转移，自动化失效恢复，自动化降级，自动化分配资源\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;安全&lt;/strong&gt;：密码，手机校验码，加密，验证码，过滤，风险控制\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h3 style=&quot;font-weight:normal;font-size:26px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n			核心要素\r\n		&lt;/h3&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			架构是“最高层次的规划，难以改变的规定”。主要关注五个要素：\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				性能\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				可用性(Availability)\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				伸缩性(Scalability)\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				扩展性(Extensibility)\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				安全性\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2 style=&quot;font-weight:normal;font-size:30px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n			架构\r\n		&lt;/h2&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			下面依次对这五个要素进行归纳\r\n		&lt;/p&gt;\r\n		&lt;h3 style=&quot;font-weight:normal;font-size:26px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n			高性能\r\n		&lt;/h3&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			性能的测试指标主要有：\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				响应时间：指应用执行一个操作需要的时间\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				并发数：指系统能够同时处理请求的数目\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				吞吐量：指单位时间内系统处理的请求数量\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				性能计数器：描述服务器或者操作系统性能的一些数据指标\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			性能测试方法：\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				性能测试\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				负载测试\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				压力测试\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				稳定性测试\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			性能优化,根据网站分层架构，可以分为三大类：\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;Web 前端性能优化&lt;/strong&gt;\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						浏览器访问优化\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								减少 http 请求\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								使用浏览器缓存\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								启用压缩\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								CSS 放在页面最上面，JavaScript 放在页面最下面\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								减少 Cookie 传输\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						CDN 加速：本质是一个缓存，一般缓存静态资源\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						反向代理\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								保护网站安全\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								通过配置缓存功能加速 Web 请求\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								实现负载均衡\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;应用服务器性能优化&lt;/strong&gt;：主要手段有 缓存、集群、异步\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						分布式缓存(&lt;em&gt;网站性能优化第一定律：优化考虑使用缓存优化性能&lt;/em&gt;)\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						异步操作(&lt;em&gt;消息队列，削峰作用&lt;/em&gt;)\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						使用集群\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						代码优化\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								多线程(设计为无状态，使用局部对象，并发访问资源使用锁)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								资源复用(单例，对象池)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								数据结构\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								垃圾回收\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;strong&gt;存储服务器性能优化&lt;/strong&gt;\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						机械硬盘 vs. 固态硬盘\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						B+ 树 vs. LSM 树\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						RAID vs. HDFS\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h3 style=&quot;font-weight:normal;font-size:26px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n			高可用\r\n		&lt;/h3&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				高可用的网站架构：目的是保证服务器硬件故障时服务依然可用、数据依然保存并能够被访问，主要手段数据和服务的冗余备份及失效转移\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				高可用的应用：显著特点是应用的无状态性\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						通过负载均衡进行无状态服务的失效转移\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						应用服务器集群的 Session 管理\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								Session 复制\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								Session 绑定\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								利用 Cookie 记录 Session\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								Session 服务器\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				高可用的服务：无状态的服务，可使用类似负载均衡的失效转移策略，此外还有如下策略\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						分级管理\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						超时设置\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						异步调用\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						服务降级\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						幂等性设计\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				高可用的数据：主要手段是数据备份和失效转移机制\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						CAP 原理\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								数据一致性(Consisitency)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								数据可用性(Availibility)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								分区耐受性(Partition Tolerance)\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						数据备份\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								冷备：缺点是不能保证数据最终一致和数据可用性\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								热备：分为异步热备和同步热备\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						失效转移：由以下三部分组成\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								失效确认\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								访问转移\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								数据恢复\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				高可用网站的软件质量保证\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						网站发布\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						自动化测试\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						预发布验证\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						代码控制\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								主干开发、分支发布\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								分支开发、主干发布\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						自动化发布\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						灰度发布\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				网站运行监控\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						监控数据采集\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								用户行为日志采集（服务器端和客户端）\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								服务器性能监控\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								运行数据报告\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						监控管理\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								警报系统\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								失效转移\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								自动优雅降级\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h3 style=&quot;font-weight:normal;font-size:26px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n			伸缩性\r\n		&lt;/h3&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			大型网站的“大型”是指：\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				用户层面：大量用户及大量访问\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				功能方面：功能庞杂，产品众多\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				技术层面：网站需要部署大量的服务器\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			伸缩性的分为如下几个方面\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				网站架构的伸缩性设计\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						不同功能进行物理分离实现伸缩\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								纵向分离(分层后分离)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								横向分离(业务分割后分离)\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						单一功能通过集群规模实现伸缩\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				应用服务器集群的伸缩性设计\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						HTTP 重定向负载均衡\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						DNS 域名解析负载均衡\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						反向代理负载均衡(在 HTTP 协议层面，应用层负载均衡)\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						IP 负载均衡(在内核进程完成数据分发)\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						数据链路层负载均衡(数据链路层修改 mac 地址，三角传输模式，LVS)\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						负载均衡算法\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								轮询(Round Robin, RR)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								加权轮询(Weighted Round Robin, WRR)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								随机(Random)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								最少链接(Least Connections)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								源地址散列(Source Hashing)\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				分布式缓存集群的伸缩性设计\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						Memcached 分布式缓存集群的访问模型\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								Memcached 客户端（包括 API，路由算法，服务器列表，通信模块）\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								Memcached 服务器集群\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						Memcached 分布式缓存集群的伸缩性挑战\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						分布式缓存的一致性 Hash 算法(一致性 Hash 环，虚拟层)\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				数据存储服务集群的伸缩性设计\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						关系数据库集群的伸缩性设计\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						NoSQL 数据库的伸缩性设计\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h3 style=&quot;font-weight:normal;font-size:26px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n			可扩展\r\n		&lt;/h3&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			系统架构设计层面的“开闭原则”\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				构建可扩展的网站架构\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				利用分布式消息队列降低耦合性\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						事件驱动架构(Event Driven Architecture)\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						分布式消息队列\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				利用分布式服务打造可复用的业务平台\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						Web Service 与企业级分布式服务\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						大型网站分布式服务的特点\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						分布式服务框架设计(Thrift, Dubbo)\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				可扩展的数据结构(如 ColumnFamily 设计)\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				利用开放平台建设网站生态圈\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h3 style=&quot;font-weight:normal;font-size:26px;color:#333333;font-family:&amp;quot;background-color:#F8F8FD;&quot;&gt;\r\n			网站的安全架构\r\n		&lt;/h3&gt;\r\n		&lt;p style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			XSS 攻击和 SQL 注入攻击是构成网站应用攻击最主要的两种手段，此外还包括 CSRF,Session 劫持等手段。\r\n		&lt;/p&gt;\r\n		&lt;ul style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;background-color:#F8F8FD;&quot;&gt;\r\n			&lt;li&gt;\r\n				攻击与防御\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						XSS 攻击：跨站点脚本攻击（Cross Site Script）\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								反射型\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								持久型\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						XSS 防御手段\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								消毒(即对某些 html 危险字符转义)\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								HttpOnly\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						注入攻击\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								SQL 注入攻击\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								OS 注入攻击\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						注入防御\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								避免被猜到数据库表结构信息\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								消毒\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								参数绑定\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						CSRF 攻击：跨站点请求伪造（Cross Site Request Forgery）\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						CSRF 防御：主要手段是识别请求者身份\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								表单 Token\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								验证码\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								Referer Check\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						其他攻击和漏洞\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								Error Code\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								HTML 注释\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								文件上传\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								路径遍历\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						Web 应用防火墙（ModSecurity）\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						网站安全漏洞扫描\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				信息加密技术及密钥安全管理\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						单向散列加密：不同输入长度的信息通过散列计算得到固定长度的输出\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								不可逆，非明文\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								可加盐(salt)增加安全性\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								输入的微小变化会导致输出完全不同\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						对称加密：加密和解密使用同一个密钥\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						非对称加密\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								信息传输：公钥加密，私钥解密\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								数字签名：私钥加密，公钥解密\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						密钥安全管理：信息安全传输是靠密钥保证的，改善手段有：\r\n						&lt;ul&gt;\r\n							&lt;li&gt;\r\n								把密钥和算法放在一个独立的服务器上\r\n							&lt;/li&gt;\r\n							&lt;li&gt;\r\n								将加解密算法放在应用系统中，密钥放在独立服务器\r\n							&lt;/li&gt;\r\n						&lt;/ul&gt;\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				信息过滤与反垃圾\r\n				&lt;ul&gt;\r\n					&lt;li&gt;\r\n						文本匹配\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						分类算法\r\n					&lt;/li&gt;\r\n					&lt;li&gt;\r\n						黑名单\r\n					&lt;/li&gt;\r\n				&lt;/ul&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n&lt;br /&gt;\r\n	&lt;/li&gt;\r\n&lt;/ol&gt;', '1484668800', '12', '转自&lt;掘金&gt;: @brianway');
INSERT INTO `sw_news` VALUES ('4', '利用Python进行深度学习的完整入门指南(附资源)', '深度学习，是人工智能领域的一个突出的话题，被众人关注已经有相当长的一段时间了。', '&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;深度学习，是人工智能领域的一个突出的话题，被众人关注已经有相当长的一段时间了。它备受关注是因为在计算机视觉（&lt;/span&gt;Computer&amp;nbsp; Vision&lt;span style=&quot;font-family:宋体;&quot;&gt;）和游戏（&lt;/span&gt;Alpha GO&lt;span style=&quot;font-family:宋体;&quot;&gt;）等领域有超越人类能力的突破&lt;/span&gt;&amp;nbsp;&lt;span style=&quot;font-family:宋体;&quot;&gt;。自上一次调查&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;color:#888888;&quot;&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;（查看调查：&lt;/span&gt;https://www.analyticsvidhya.com/blog/2014/06/deep-learning-attention/&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;）&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;以来，对于深度学习的关注又出现了大幅增加的趋势。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;下图是谷歌趋势向我们所展示的：&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;&lt;img src=&quot;http://mmbiz.qpic.cn/mmbiz_png/wc7YNPm3YxWBvsbicQJtME9FkQRWr0tEiaIUAv5ZPlIlKa2fRsugA8rOjpf2jb9USSgYQWTftmZgFu2Rooa62PYA/640?wx_fmt=png&amp;amp;tp=webp&amp;amp;wxfrom=5&amp;amp;wx_lazy=1&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;如果你对这个话题感兴趣的话，本文是一个很好的非技术性的介绍。如果你有兴趣了解关于深度学习的最新趋势，本文是一个很全面的汇总。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;font-family:宋体;&quot;&gt;在这里，我们的目标是为新手和想进一步探索深度学习的人们提供一个学习路径。那么，你准备好踏上征服深度学习的征程了吗？我们上路吧。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:15px;color:#3DA742;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;步骤&lt;/span&gt;0&lt;span style=&quot;font-family:宋体;&quot;&gt;：出征准备&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;建议在进入深入学习领域之前，应该了解机器学习的基本知识。“机器学习的学习路径”（&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;https://www.analyticsvidhya.com/learning-path-learn-machine-learning/&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;）是一个完整的资源，让你开始了解该领域。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;如果你想要一个较短的版本，请看下面：&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;基础数学，资源&lt;/span&gt;1&lt;span style=&quot;font-family:宋体;&quot;&gt;：“数学&lt;/span&gt;|&lt;span style=&quot;font-family:宋体;&quot;&gt;可汗学院（&lt;/span&gt;Khan academy&lt;span style=&quot;font-family:宋体;&quot;&gt;，&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;https://www.khanacademy.org/math&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;）”（尤其是微积分、概率论和线性代数）&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;&quot;&gt;Python&lt;span style=&quot;font-family:宋体;&quot;&gt;基础，资源：“计算机科学入门”（&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;https://www.datacamp.com/community/open-courses/introduction-to-python-machine-learning-with-analytics-vidhya-hackathons#gs.null&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;），&lt;/span&gt;EDX&lt;span style=&quot;font-family:宋体;&quot;&gt;课程&lt;/span&gt;&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;统计学基础，资源：“统计入门”（&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;https://www.udacity.com/course/intro-to-statistics--st101&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;），&lt;/span&gt;Udacity&lt;span style=&quot;font-family:宋体;&quot;&gt;的课程&lt;/span&gt;&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;机器学习基础，资源：“机器学习入门”（&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;https://www.udacity.com/course/intro-to-machine-learning--ud120&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;），&lt;/span&gt;Udacity&lt;span style=&quot;font-family:宋体;&quot;&gt;的课程&lt;/span&gt;&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;时间：建议&lt;/span&gt;2-6&lt;span style=&quot;font-family:宋体;&quot;&gt;个月&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&amp;nbsp;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:15px;color:#3DA742;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;步骤&lt;/span&gt;1&lt;span style=&quot;font-family:宋体;&quot;&gt;：设置好你的机器&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;在继续下一步之前，请确保有支撑硬件。一般建议应该至少有：&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;一个足够好的&lt;/span&gt;GPU&lt;span style=&quot;font-family:宋体;&quot;&gt;（&lt;/span&gt;4+ GB&lt;span style=&quot;font-family:宋体;&quot;&gt;），最好是&lt;/span&gt;Nvidia&lt;span style=&quot;font-family:宋体;&quot;&gt;的&lt;/span&gt;&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;一个够用的&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;CPU&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;（如英特尔酷睿&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;i3&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;可以，而英特尔&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Pentium&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;不行）&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;&quot;&gt;4GB&amp;nbsp;&lt;span style=&quot;font-family:宋体;&quot;&gt;内存或取决于数据集。&lt;/span&gt;&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&amp;nbsp;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;如果仍然不确定，可以看一下这个硬件指南&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;http://timdettmers.com/2015/03/09/deep-learning-hardware-guide/&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;PS&lt;span style=&quot;font-family:宋体;&quot;&gt;：如果你是一个铁杆游戏玩家（当然不只是糖果粉碎机玩家！），你可能已经有了所需&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;font-family:宋体;&quot;&gt;硬件。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;如果没有所需硬件，可以购买或租用一个亚马逊网页服务（&lt;/span&gt;AWS&lt;span style=&quot;font-family:宋体;&quot;&gt;，&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;https://aws.amazon.com&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;）。这里有个利用&lt;/span&gt;AWS&lt;span style=&quot;font-family:宋体;&quot;&gt;进行深度学习的好指南：&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;http://cs231n.github.io/aws-tutorial/&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;注意：在这个阶段不用安装任何深度学习的学习库，这些在步骤&lt;/span&gt;3&lt;span style=&quot;font-family:宋体;&quot;&gt;中做。&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&amp;nbsp;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;color:#3DA742;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;步骤&lt;/span&gt;&lt;span style=&quot;color:#3DA742;font-size:15px;line-height:1.75em;&quot;&gt;2&lt;/span&gt;&lt;span style=&quot;color:#3DA742;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：尝试一下&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;现在有了足够的预备知识，可以进一步了解深度学习了。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;按照个人喜好，可以关注：&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;博客：&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:1.75em;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;（资源&lt;/span&gt;1&lt;span style=&quot;font-family:宋体;&quot;&gt;：“深度学习的基础”&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:1.75em;font-size:15px;&quot;&gt;https://www.analyticsvidhya.com/blog/2016/03/introduction-deep-learning-fundamentals-neural-networks/&lt;/span&gt;&lt;span style=&quot;line-height:1.75em;font-family:宋体;font-size:15px;&quot;&gt;，&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;资源&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;2&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：“黑客的神经网络指南”&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;&amp;nbsp;http://karpathy.github.io/neuralnets/&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;）&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;视频：“简化的深度学习”&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;https://www.youtube.com/watch?v=b99UVkWzYTQ&amp;amp;list=PLjJh1vlSEYgvGod9wWiydumYl8hOXixNu&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;教科书：《神经网络和深度学习》&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://neuralnetworksanddeeplearning.com&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;除了这些先决条件，还应该知道流行的深度学习程序库和运行它们的语言。这是一个（非全面）列表（更全面的列表请查看维基页面）：&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Caffe&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://caffe.berkeleyvision.org&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;DeepLearning4j&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://deeplearning4j.org&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Tensorflow&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;https://www.tensorflow.org&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Theano&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://www.deeplearning.net/software/theano/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Torch&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://torch.ch&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;其他一些著名的库包括：&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;&quot;&gt;Mocha&lt;span style=&quot;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;http://mochajl.readthedocs.io/en/latest/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;neon&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://neon.nervanasys.com/index.html/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;H2O&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://www.h2o.ai/verticals/algos/deep-learning/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;MXNet&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;https://github.com/dmlc/mxnet&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Keras&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;&amp;nbsp;https://keras.io&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Lasagne&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://lasagne.readthedocs.io/en/latest/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;line-height:1.75em;font-size:15px;&quot;&gt;Nolearn&lt;span style=&quot;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:1.75em;font-size:15px;&quot;&gt;https://github.com/dnouri/nolearn&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&amp;nbsp;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;line-height:1.75em;font-family:宋体;font-size:15px;&quot;&gt;这里有一个根据语言分类的深度学习库列表：&lt;/span&gt;&lt;span style=&quot;line-height:1.75em;font-size:15px;&quot;&gt;http://www.teglor.com/b/deep-learning-libraries-language-cm569/&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;建议看斯坦福的&lt;/span&gt;cs231n&lt;span style=&quot;font-family:宋体;&quot;&gt;课程第&lt;/span&gt;12&lt;span style=&quot;font-family:宋体;&quot;&gt;讲：流行的深度学习库概述&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;http://cs231n.stanford.edu/slides/winter1516_lecture12.pdf&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;时间：建议&lt;/span&gt;1-3&lt;span style=&quot;font-family:宋体;&quot;&gt;周&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:15px;color:#3DA742;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;步骤&lt;/span&gt;3&lt;span style=&quot;font-family:宋体;&quot;&gt;：选择你自己的探险之路&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;font-family:宋体;&quot;&gt;现在有趣的部分来了！深度学习已被应用在各个领域并带来了最先进成果。为了进入到这个领域，你，读者，需要选择一条合适自己的路走。这应该是个实践经验，那样你就可以在目前所理解的之上获得一个合适的基础。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;注意：每条路径包含一个入门简介的博客，一个实践项目，项目所需的深度学习的程序库和辅助课程。首先理解简介，然后安装所需的程序库，开始项目工作。如果你在这其中遇到任何困难，利用相关的课程以获得支持。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;深度学习之计算机视觉&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;入门简介：“&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;DL forComputer Vision&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;”电脑视觉博客&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;https://www.analyticsvidhya.com/blog/2016/04/deep-learning-computer-vision-introduction-convolution-neural-networks/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;项目：“&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;FacialKeypoint Detection&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;”人脸关键点检测教程&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://danielnouri.org/notes/2014/12/17/using-convolutional-neural-nets-to-detect-facial-keypoints-tutorial/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;所需程序库：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Nolearn&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;，&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;https://github.com/dnouri/nolearn#installation&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;相关课程：“&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;cs231n&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;卷积神经网络视觉识别”&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://cs231n.stanford.edu&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&amp;nbsp;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;深度学习之自然语言处理&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;line-height:1.75em;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;入门简介：“&lt;/span&gt;DeepLearning, NLP, and Representations&lt;span style=&quot;font-family:宋体;&quot;&gt;”（“深度学习，自然语言处理与呈现”博客，&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:1.75em;font-size:15px;&quot;&gt;http://colah.github.io/posts/2014-07-NLP-RNNs-Representations/&lt;/span&gt;&lt;span style=&quot;line-height:1.75em;font-family:宋体;font-size:15px;&quot;&gt;）&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-family:宋体;font-size:15px;line-height:1.75em;&quot;&gt;项目：“深度学习聊天机器人”：&lt;/span&gt;\r\n			&lt;/p&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-family:宋体;font-size:15px;line-height:1.75em;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;“第一部分”&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://www.wildml.com/2016/04/deep-learning-for-chatbots-part-1-introduction/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;“第二部分”&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://www.wildml.com/2016/07/deep-learning-for-chatbots-2-retrieval-based-model-tensorflow/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;所需程序库：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;tensorflow&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;https://github.com/dennybritz/chatbot-retrieval/#setup&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;相关课程：“&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;cs224d&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：深度学习在自然语言处理方面的应用”&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;&amp;nbsp;http://cs224d.stanford.edu&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;深度学习之语音&lt;/span&gt;/&lt;span style=&quot;font-family:宋体;&quot;&gt;音频识别&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;入门简介：“深度演讲：深度学习的经验教训”新闻和相应的视频。&lt;/span&gt;http://usa.baidu.com/deep-speech-lessons-from-deep-learning/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;项目：“用&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Magenta&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;生成音乐（&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;tensorflow&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;）”&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;https://magenta.tensorflow.org/2016/06/10/recurrent-neural-network-generation-tutorial/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n			&lt;p&gt;\r\n				&lt;br /&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;所需程序库：&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Magenta&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;，&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;https://github.com/tensorflow/magenta#installation&lt;/span&gt;\r\n			&lt;/p&gt;\r\n			&lt;p&gt;\r\n				&lt;br /&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;相关课程：“深度学习（&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;2016&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;春季），&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;cilvr&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;实验室&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;@&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;纽约&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://cilvr.cs.nyu.edu/doku.php?id=courses:deeplearning2016:start&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;深度学习之强化学习&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;ul style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;入门介绍与项目：“深度强化学习：来自&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;Pixels&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;的乒乓球游戏”&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;&amp;nbsp;http://karpathy.github.io/2016/05/31/rl/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n			&lt;p&gt;\r\n				&lt;br /&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;line-height:1.75em;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;所需程序库：没有要求。但是需要&lt;/span&gt;openAI gym&amp;nbsp;&lt;span style=&quot;font-family:宋体;&quot;&gt;（&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:1.75em;font-size:15px;&quot;&gt;https://github.com/openai/gym#installation&lt;/span&gt;&lt;span style=&quot;line-height:1.75em;font-family:宋体;font-size:15px;&quot;&gt;）来测试你的模型。&lt;/span&gt;\r\n			&lt;/p&gt;\r\n			&lt;p&gt;\r\n				&lt;br /&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n		&lt;li&gt;\r\n			&lt;p&gt;\r\n				&lt;span style=&quot;line-height:1.75em;font-family:宋体;font-size:15px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;相关课程：“&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;cs294&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：深度强化学习”&lt;/span&gt;&lt;span style=&quot;font-size:15px;line-height:1.75em;&quot;&gt;http://rll.berkeley.edu/deeprlcourse/&lt;/span&gt;\r\n			&lt;/p&gt;\r\n		&lt;/li&gt;\r\n	&lt;/ul&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;时间：建议&lt;/span&gt;1-2&lt;span style=&quot;font-family:宋体;&quot;&gt;个月&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;color:#3DA742;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;color:#3DA742;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;步骤&lt;/span&gt;&lt;span style=&quot;color:#3DA742;font-size:15px;line-height:1.75em;&quot;&gt;4&lt;/span&gt;&lt;span style=&quot;color:#3DA742;font-size:15px;line-height:1.75em;font-family:宋体;&quot;&gt;：深入深度学习&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;现在（几乎）能够在深度学习名人堂上占一个坑！前面的道路是漫长的和深刻的（双关语），其实主要是还未探索过的。现在是靠你自己熟悉和使用这个新技能的时候。这里有一些技巧，可以提升你的技能。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;选择一个不同的路径重复上述步骤。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;深度学习之以上没提到的！（例如：交易的深度学习，为优化能源效率的深度学习&lt;/span&gt;https://deepmind.com/blog/?t=1&lt;span style=&quot;font-family:宋体;&quot;&gt;）&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;使用你新学到的技能来创建一个东西（请记得，力量越大，责任越大，&lt;/span&gt;https://xkcd.com/1613/&lt;span style=&quot;font-family:宋体;&quot;&gt;）&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;测试你的深度学习技能（如&lt;/span&gt;Kaggle&lt;span style=&quot;font-family:宋体;&quot;&gt;，&lt;/span&gt;&amp;nbsp;https://www.kaggle.com/competitions&lt;span style=&quot;font-family:宋体;&quot;&gt;）&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;参与深度学习社区（如谷歌群&lt;/span&gt;&amp;nbsp;https://plus.google.com/u/0/communities/112866381580457264725&lt;span style=&quot;font-family:宋体;&quot;&gt;，&lt;/span&gt;DL subreddithttps://www.reddit.com/r/deeplearning/&lt;span style=&quot;font-family:宋体;&quot;&gt;）&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-size:15px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;关注最新的研究成果及研究人员。（如“&lt;/span&gt;RE.WORK DL Summit&amp;nbsp;&lt;span style=&quot;font-family:宋体;&quot;&gt;”，&lt;/span&gt;https://www.re-work.co/events/deep-learning-sanfran-2016&lt;span style=&quot;font-family:宋体;&quot;&gt;”）&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;font-size:15px;&quot;&gt;时间：建议——无上限！&lt;/span&gt;\r\n	&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;/span&gt;\r\n&lt;/p&gt;', '1484668800', '5', '转自：大数据文摘 |bigdatadigest');
INSERT INTO `sw_news` VALUES ('6', 'Ajax入门', '本文讲解了ajax的历史，工作原理以及优缺点，对XMLHttpRequest对象进行了详细的讲解，并使用原生js实现了一个ajax对象以方便日常开始使用。', '&lt;h2 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	前言\r\n&lt;/h2&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-weight:700;&quot;&gt;总括：&lt;/span&gt;&amp;nbsp;本文讲解了ajax的历史，工作原理以及优缺点，对XMLHttpRequest对象进行了详细的讲解，并使用原生js实现了一个ajax对象以方便日常开始使用。\r\n&lt;/p&gt;\r\n&lt;ul style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;li&gt;\r\n		damonare的ajax库：&lt;a href=&quot;https://github.com/damonare/ajax/tree/master&quot; target=&quot;_blank&quot;&gt;damonare的ajax库&lt;/a&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		原文博客地址：&lt;a href=&quot;https://damonare.github.io/2017/01/18/%E4%BD%A0%E7%9C%9F%E7%9A%84%E6%87%82ajax%E5%90%97%EF%BC%9F/#more&quot; target=&quot;_blank&quot;&gt;你真的懂ajax吗？&lt;/a&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		知乎专栏&amp;amp;&amp;amp;简书专题：&lt;a href=&quot;https://zhuanlan.zhihu.com/damonare&quot; target=&quot;_blank&quot;&gt;前端进击者（知乎）&lt;/a&gt;&amp;amp;&amp;amp;&lt;a href=&quot;http://www.jianshu.com/collection/bbaa63e264f5&quot; target=&quot;_blank&quot;&gt;前端进击者（简书）&lt;/a&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		博主博客地址：&lt;a href=&quot;http://damonare.cn/&quot; target=&quot;_blank&quot;&gt;Damonare的个人博客&lt;/a&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-weight:700;&quot;&gt;古之立大事者，不惟有超世之才，亦必有坚忍不拔之志。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;h2 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	正文\r\n&lt;/h2&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	相信每个前端程序员日常工作中都避免不了的工作就是和后端联调，联调自然就避免不了使用ajax，但我相信，不管是使用jquery封装的ajax方法还是使用vue的插件vue-resource的程序员，真正对于ajax有过深入探究的并不多，我们更多的是为了使用而使用，至于它的原理往往因为即使不了解依旧能做出东西而懒得去看，我们都被轮子们惯坏了。根据&lt;a href=&quot;https://zh.wikipedia.org/wiki/%E5%B8%95%E9%9B%B7%E6%89%98%E6%B3%95%E5%88%99&quot; target=&quot;_blank&quot;&gt;二八定律&lt;/a&gt;，即任何一组东西中，最重要的只占其中一小部分，约20%，其余80%的尽管是多数，却是次要的。因 此，如果想挤进那20%的行列，就要学到一般人学不到的深度，学到一般人学不了的东西。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	好的，现在我们从头来说一下ajax。\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	Ajax简介\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	在上世纪90年代，几乎所有的网站都由HTML页面实现，服务器处理每一个用户请求都需要重新加载网页。形式是怎样的呢？就比如说你在浏览器上登录自己的微博账号，填完了表单，点击登录按钮，一次&quot;完整&quot;的HTTP请求就此触发，服务器发现你的登录密码不对头，立马把网页原原本本的返回给你，在用户看来呢，就是一次重新加载的过程！用户体验极差！而且这个做法浪费了许多带宽，因为在前后两个页面中的大部分HTML码往往是相同的。由于每次应用的沟通都需要向服务器发送请求，应用的回应时间依赖于服务器的回应时间。这导致了用户界面的回应比本机应用慢得多。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	到了2005年，google率先在它的应用(诸如google地图、gmail)里使用了ajax技术，这才让这项技术正式风靡开来。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	如今它的应用已经十分广泛：\r\n&lt;/p&gt;\r\n&lt;ul style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;li&gt;\r\n		运用&lt;a href=&quot;https://zh.wikipedia.org/wiki/XHTML&quot; target=&quot;_blank&quot;&gt;XHTML&lt;/a&gt;+&lt;a href=&quot;https://zh.wikipedia.org/wiki/CSS&quot; target=&quot;_blank&quot;&gt;CSS&lt;/a&gt;来表达信息；\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		运用&lt;a href=&quot;https://zh.wikipedia.org/wiki/JavaScript&quot; target=&quot;_blank&quot;&gt;JavaScript&lt;/a&gt;操作&lt;a href=&quot;https://zh.wikipedia.org/wiki/%E6%96%87%E4%BB%B6%E7%89%A9%E4%BB%B6%E6%A8%A1%E5%9E%8B&quot; target=&quot;_blank&quot;&gt;DOM&lt;/a&gt;（Document Object Model）来运行动态效果；\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		运用&lt;a href=&quot;https://zh.wikipedia.org/wiki/XML&quot; target=&quot;_blank&quot;&gt;XML&lt;/a&gt;和&lt;a href=&quot;https://zh.wikipedia.org/wiki/XSLT&quot; target=&quot;_blank&quot;&gt;XSLT&lt;/a&gt;操作数据；\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		运用&lt;a href=&quot;https://zh.wikipedia.org/wiki/XMLHttpRequest&quot; target=&quot;_blank&quot;&gt;XMLHttpRequest&lt;/a&gt;或新的Fetch API与&lt;a href=&quot;https://zh.wikipedia.org/wiki/%E7%B6%B2%E9%A0%81%E4%BC%BA%E6%9C%8D%E5%99%A8&quot; target=&quot;_blank&quot;&gt;网页服务器&lt;/a&gt;进行异步数据交换；\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		注意：AJAX与&lt;a href=&quot;https://zh.wikipedia.org/wiki/Flash&quot; target=&quot;_blank&quot;&gt;Flash&lt;/a&gt;、&lt;a href=&quot;https://zh.wikipedia.org/wiki/Silverlight&quot; target=&quot;_blank&quot;&gt;Silverlight&lt;/a&gt;和&lt;a href=&quot;https://zh.wikipedia.org/wiki/Java_Applet&quot; target=&quot;_blank&quot;&gt;Java Applet&lt;/a&gt;等&lt;a href=&quot;https://zh.wikipedia.org/wiki/RIA&quot; target=&quot;_blank&quot;&gt;RIA&lt;/a&gt;技术是有区分的。\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	Ajax工作原理\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	Ajax的工作原理相当于在用户和服务器之间加了一个中间层(ajax引擎),使用户操作与服务器响应异步化。并不是所有的用户请求都提交给服务器,像—些数据验证(比如判断用户是否输入了数据)和数据处理(比如判断用户输入数据是否是数字)等都交给Ajax引擎自己来做, 只有确定需要从服务器读取新数据时再由Ajax引擎代为向服务器提交请求。把这些交给了Ajax引擎，用户操作起来也就感觉更加流畅了。\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	Ajax优缺点\r\n&lt;/h3&gt;\r\n&lt;h4 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	Ajax的优点\r\n&lt;/h4&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	1.无刷新更新数据。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	AJAX最大优点就是能在不刷新整个页面的前提下与服务器通信维护数据。这使得Web应用程序更为迅捷地响应用户交互，并避免了在网络上发送那些没有改变的信息，减少用户等待时间，带来非常好的用户体验。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	2.异步与服务器通信。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	AJAX使用异步方式与服务器通信，不需要打断用户的操作，具有更加迅速的响应能力。优化了Browser和Server之间的沟通，减少不必要的数据传输、时间及降低网络上数据流量。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	3.前端和后端负载平衡。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	AJAX可以把以前一些服务器负担的工作转嫁到客户端，利用客户端闲置的能力来处理，减轻服务器和带宽的负担，节约空间和宽带租用成本。并且减轻服务器的负担，AJAX的原则是“按需取数据”，可以最大程度的减少冗余请求和响应对服务器造成的负担，提升站点性能。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	4.基于标准被广泛支持。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	AJAX基于标准化的并被广泛支持的技术，不需要下载浏览器插件或者小程序，但需要客户允许JavaScript在浏览器上执行。随着Ajax的成熟，一些简化Ajax使用方法的程序库也相继问世。同样，也出现了另一种辅助程序设计的技术，为那些不支持JavaScript的用户提供替代功能。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	5.界面与应用分离。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	Ajax使WEB中的界面与应用分离（也可以说是数据与呈现分离），有利于分工合作、减少非技术人员对页面的修改造成的WEB应用程序错误、提高效率、也更加适用于现在的发布系统。\r\n&lt;/p&gt;\r\n&lt;h4 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	Ajax缺点\r\n&lt;/h4&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	1.AjAX干掉了Back和加入收藏书签功能，即对浏览器机制的破坏。\r\n&lt;/p&gt;\r\n&lt;blockquote style=&quot;color:#666666;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p&gt;\r\n		对应用Ajax最主要的批评就是，它可能破坏浏览器的后退与加入收藏书签功能。在动态更新页面的情况下，用户无法回到前一个页面状态，这是因为浏览器仅能记下历史记录中的&lt;a href=&quot;https://zh.wikipedia.org/w/index.php?title=%E9%9D%99%E6%80%81%E9%A1%B5%E9%9D%A2&amp;amp;action=edit&amp;amp;redlink=1&quot; target=&quot;_blank&quot;&gt;静态页面&lt;/a&gt;。一个被完整读入的页面与一个已经被动态修改过的页面之间的可能差别非常微妙；用户通常都希望单击后退按钮，就能够取消他们的前一次操作，但是在Ajax应用程序中，却无法这样做。不过开发者已想出了种种办法来解决这个问题，&lt;a href=&quot;https://zh.wikipedia.org/wiki/HTML5&quot; target=&quot;_blank&quot;&gt;HTML5&lt;/a&gt;&amp;nbsp;之前的方法大多是在用户单击后退按钮访问历史记录时，通过创建或使用一个隐藏的IFRAME来重现页面上的变更。（例如，当用户在Google Maps中单击后退时，它在一个隐藏的&lt;a href=&quot;https://zh.wikipedia.org/w/index.php?title=IFRAME&amp;amp;action=edit&amp;amp;redlink=1&quot; target=&quot;_blank&quot;&gt;IFRAME&lt;/a&gt;中进行搜索，然后将搜索结果反映到Ajax元素上，以便将应用程序状态恢复到当时的状态）。\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		关于无法将状态加入收藏或书签的问题，&lt;a href=&quot;https://zh.wikipedia.org/wiki/HTML5&quot; target=&quot;_blank&quot;&gt;HTML5&lt;/a&gt;之前的一种方式是使用&lt;a href=&quot;https://zh.wikipedia.org/wiki/URL&quot; target=&quot;_blank&quot;&gt;URL&lt;/a&gt;片断标识符（通常被称为&lt;a href=&quot;https://zh.wikipedia.org/wiki/%E9%94%9A%E7%82%B9&quot; target=&quot;_blank&quot;&gt;锚点&lt;/a&gt;，即URL中#后面的部分）来保持追踪，允许用户回到指定的某个应用程序状态。（许多浏览器允许JavaScript动态更新锚点，这使得Ajax应用程序能够在更新显示内容的同时更新锚点。）&lt;a href=&quot;https://zh.wikipedia.org/wiki/HTML5&quot; target=&quot;_blank&quot;&gt;HTML5&lt;/a&gt;&amp;nbsp;以后可以直接操作浏览历史，并以字符串形式存储网页状态，将网页加入网页收藏夹或书签时状态会被隐形地保留。上述两个方法也可以同时解决无法后退的问题。\r\n	&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	2.AJAX的安全问题。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	AJAX技术给用户带来很好的用户体验的同时也对IT企业带来了新的安全威胁，Ajax技术就如同对企业数据建立了一个直接通道。这使得开发者在不经意间会暴露比以前更多的数据和服务器逻辑。Ajax的逻辑可以对客户端的安全扫描技术隐藏起来，允许黑客从远端服务器上建立新的攻击。还有Ajax也难以避免一些已知的安全弱点，诸如跨站点脚步攻击、SQL注入攻击和基于Credentials的安全漏洞等等。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	3.因为网络延迟需要给用户提供必要提示\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	进行Ajax开发时，网络延迟——即用户发出请求到服务器发出响应之间的间隔——需要慎重考虑。如果不给予用户明确的回应，没有恰当的预读数据，或者对XMLHttpRequest的不恰当处理，都会使用户感到厌烦。通常的解决方案是，使用一个可视化的组件来告诉用户系统正在进行后台操作并且正在读取数据和内容。\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	XMLhttpRequest介绍\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	Ajax(Asynchronous JavaScript and XML)不是指一种单一的技术，而是有机地利用了一系列相关的技术。虽然其名称包含XML，但实际上数据格式可以由&lt;a href=&quot;https://zh.wikipedia.org/wiki/JSON&quot; target=&quot;_blank&quot;&gt;JSON&lt;/a&gt;代替，进一步减少数据量，形成所谓的AJAJ。为了使用JavaScript向服务器发出&amp;nbsp;&lt;a href=&quot;https://developer.mozilla.org/en/HTTP&quot; target=&quot;_blank&quot;&gt;HTTP&lt;/a&gt;&amp;nbsp;请求，需要一个提供此功能的类的实例。这就是XMLHttpRequest的由来。这样的类最初是在Internet Explorer中作为一个名为XMLHTTP的ActiveX对象引入的。然后，Mozilla，Safari和其他浏览器，实现一个XMLHttpRequest类，支持Microsoft的原始ActiveX对象的方法和属性。同时微软也实现了XMLHttpRequest。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	显而易见XMLHttpRequest类是重中之重了。\r\n&lt;/p&gt;\r\n&lt;h4 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	XMLhttpRequest属性\r\n&lt;/h4&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	onreadystatechange\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	一个JavaScript函数对象，当readyState属性改变时会调用它。回调函数会在user interface线程中调用。\r\n&lt;/p&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	readyState\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	HTTP 请求的状态.当一个 XMLHttpRequest 初次创建时，这个属性的值从 0 开始，直到接收到完整的 HTTP 响应，这个值增加到 4。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	5 个状态中每一个都有一个相关联的非正式的名称，下表列出了状态、名称和含义：\r\n&lt;/p&gt;\r\n&lt;table style=&quot;font-size:0.8em;width:668px;border:1px solid #F6F6F6;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;th&gt;\r\n				状态\r\n			&lt;/th&gt;\r\n			&lt;th&gt;\r\n				名称\r\n			&lt;/th&gt;\r\n			&lt;th&gt;\r\n				描述\r\n			&lt;/th&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				0\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				Uninitialized\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				初始化状态。XMLHttpRequest 对象已创建或已被 abort() 方法重置。\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				1\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				Open\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				open() 方法已调用，但是 send() 方法未调用。请求还没有被发送。\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				2\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				Sent\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				Send() 方法已调用，HTTP 请求已发送到 Web 服务器。未接收到响应。\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				3\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				Receiving\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				所有响应头部都已经接收到。响应体开始接收但未完成。\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				4\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				Loaded\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				HTTP 响应已经完全接收。\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	readyState 的值不会递减，除非当一个请求在处理过程中的时候调用了 abort() 或 open() 方法。每次这个属性的值增加的时候，都会触发 onreadystatechange 事件句柄。\r\n&lt;/p&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	responseText\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	目前为止为服务器接收到的响应体（不包括头部），或者如果还没有接收到数据的话，就是空字符串。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	如果 readyState 小于 3，这个属性就是一个空字符串。当 readyState 为 3，这个属性返回目前已经接收的响应部分。如果 readyState 为 4，这个属性保存了完整的响应体。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	如果响应包含了为响应体指定字符编码的头部，就使用该编码。否则，假定使用 Unicode UTF-8。\r\n&lt;/p&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	responseXML\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	对请求的响应，解析为 XML 并作为 Document 对象返回。\r\n&lt;/p&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	status\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	由服务器返回的 HTTP 状态代码，如 200 表示成功，而 404 表示 &quot;Not Found&quot; 错误。当 readyState 小于 3 的时候读取这一属性会导致一个异常。\r\n&lt;/p&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	statusText\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	这个属性用名称而不是数字指定了请求的 HTTP 的状态代码。也就是说，当状态为 200 的时候它是 &quot;OK&quot;，当状态为 404 的时候它是 &quot;Not Found&quot;。和 status 属性一样，当 readyState 小于 3 的时候读取这一属性会导致一个异常。\r\n&lt;/p&gt;\r\n&lt;h4 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	XMLHttpRequest方法\r\n&lt;/h4&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	abort()\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	取消当前响应，关闭连接并且结束任何未决的网络活动。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	这个方法把 XMLHttpRequest 对象重置为 readyState 为 0 的状态，并且取消所有未决的网络活动。例如，如果请求用了太长时间，而且响应不再必要的时候，可以调用这个方法。\r\n&lt;/p&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	getAllResponseHeaders()\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	把 HTTP 响应头部作为未解析的字符串返回。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	如果 readyState 小于 3，这个方法返回 null。否则，它返回服务器发送的所有 HTTP 响应的头部。头部作为单个的字符串返回，一行一个头部。每行用换行符 &quot;\\r\\n&quot; 隔开。\r\n&lt;/p&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	getResponseHeader()\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	返回指定的 HTTP 响应头部的值。其参数是要返回的 HTTP 响应头部的名称。可以使用任何大小写来制定这个头部名字，和响应头部的比较是不区分大小写的。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	该方法的返回值是指定的 HTTP 响应头部的值，如果没有接收到这个头部或者 readyState 小于 3 则为空字符串。如果接收到多个有指定名称的头部，这个头部的值被连接起来并返回，使用逗号和空格分隔开各个头部的值。\r\n&lt;/p&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	open()\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	初始化一个请求. 该方法用于JavaScript代码中;如果是本地代码, 使用&amp;nbsp;&lt;a href=&quot;https://developer.mozilla.org/zh-cn/nsIXMLHttpRequest#openRequest(&quot; target=&quot;_blank&quot;&gt;openRequest()&lt;/a&gt;)方法代替.\r\n&lt;/p&gt;\r\n&lt;blockquote style=&quot;color:#666666;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-weight:700;&quot;&gt;注意:&lt;/span&gt;&amp;nbsp;在一个已经激活的request下（已经调用open()或者openRequest()方法的request）再次调用这个方法相当于调用了abort（）方法。\r\n	&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	参数\r\n&lt;/p&gt;\r\n&lt;ul style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			method\r\n		&lt;/p&gt;\r\n		&lt;p&gt;\r\n			请求所使用的HTTP方法; 例如 &quot;GET&quot;, &quot;POST&quot;, &quot;PUT&quot;, &quot;DELETE&quot;等. 如果下个参数是非HTTP(S)的URL,则忽略该参数.\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			url\r\n		&lt;/p&gt;\r\n		&lt;p&gt;\r\n			该请求所要访问的URL\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			async\r\n		&lt;/p&gt;\r\n		&lt;p&gt;\r\n			一个可选的布尔值参数，默认为true,意味着是否执行异步操作，如果值为false,则send()方法不会返回任何东西，直到接受到了服务器的返回数据。如果为值为true，一个对开发者透明的通知会发送到相关的事件监听者。这个值必须是true,如果multipart 属性是true，否则将会出现一个意外。\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			user\r\n		&lt;/p&gt;\r\n		&lt;p&gt;\r\n			用户名,可选参数,为授权使用;默认参数为空string.\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			password\r\n		&lt;/p&gt;\r\n		&lt;p&gt;\r\n			密码,可选参数,为授权使用;默认参数为空string.\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	send()\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	发送 HTTP 请求，使用传递给 open() 方法的参数，以及传递给该方法的可选请求体。\r\n&lt;/p&gt;\r\n&lt;h5 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	setRequestHeader()\r\n&lt;/h5&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	向一个打开但未发送的请求设置或添加一个 HTTP 请求(设置请求头)。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	参数\r\n&lt;/p&gt;\r\n&lt;ul style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			header\r\n		&lt;/p&gt;\r\n		&lt;p&gt;\r\n			将要被赋值的请求头名称\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			value\r\n		&lt;/p&gt;\r\n		&lt;p&gt;\r\n			给指定的请求头赋的值\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	Ajax原生js实现\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	下面是使用原生js写的ajax：\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n&lt;pre class=&quot;prettyprint lang-js&quot;&gt;var ajax = {};\r\najax.httpRequest = function () {\r\n    //判断是否支持XMLHttpRequest对象\r\n    if (window.XMLHttpRequest) {\r\n        return new XMLHttpRequest();\r\n    }\r\n    //兼容IE浏览器\r\n    var versions = [\r\n        &quot;MSXML2.XmlHttp.6.0&quot;,\r\n        &quot;MSXML2.XmlHttp.5.0&quot;,\r\n        &quot;MSXML2.XmlHttp.4.0&quot;,\r\n        &quot;MSXML2.XmlHttp.3.0&quot;,\r\n        &quot;MSXML2.XmlHttp.2.0&quot;,\r\n        &quot;Microsoft.XmlHttp&quot;\r\n    ];\r\n    //定义局部变量xhr,储存IE浏览器的ActiveXObject对象\r\n    var xhr;\r\n    for (var i = 0; i &amp;lt; versions.length; i++) {\r\n        try {\r\n            xhr = new ActiveXObject(versions[i]);\r\n            break;\r\n        } catch (e) {\r\n        }\r\n    }\r\n    return xhr;\r\n};\r\n\r\najax.send = function (url, callback, method, data, async) {\r\n    //默认异步\r\n    if (async === undefined) {\r\n        async = true;\r\n    }\r\n    var httpRequest = ajax.httpRequest();\r\n    //初始化HTTP请求\r\n    httpRequest.open(method, url, async);\r\n    //onreadystatechange函数对象\r\n    httpRequest.onreadystatechange = function () {\r\n        //readyState 的值等于4，从服务器拿到了数据\r\n        if (httpRequest.readyState == 4) {\r\n            //回调服务器响应数据\r\n            callback(httpRequest.responseText)\r\n        }\r\n    };\r\n    if (method == \'POST\') {\r\n          //给指定的HTTP请求头赋值\r\n        httpRequest.setRequestHeader(\'Content-type\', \'application/x-www-form-urlencoded\');\r\n    }\r\n      //发送HTTP请求\r\n    httpRequest.send(data);\r\n};\r\n//实现GET请求\r\najax.get = function (url, data, callback, async) {\r\n    var query = [];\r\n    for (var key in data) {\r\n        query.push(encodeURIComponent(key) + \'=\' + encodeURIComponent(data[key]));\r\n    }\r\n    ajax.send(url + (query.length ? \'?\' + query.join(\'&amp;amp;\') : \'\'), callback, \'GET\', null, async)\r\n};\r\n//实现POST请求\r\najax.post = function (url, data, callback, async) {\r\n    var query = [];\r\n    for (var key in data) {\r\n        query.push(encodeURIComponent(key) + \'=\' + encodeURIComponent(data[key]));\r\n    }\r\n    ajax.send(url, callback, \'POST\', query.join(\'&amp;amp;\'), async)\r\n};&lt;/pre&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		如果你使用jquery或是zepto很大部分是因为它的ajax兼容性高的缘故，不妨试试这个：&lt;a href=&quot;https://github.com/damonare/ajax/tree/master&quot; target=&quot;_blank&quot;&gt;damonare的ajax库&lt;/a&gt;，喜欢给个star也是可以的。\r\n	&lt;/p&gt;\r\n	&lt;h2 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n		后记\r\n	&lt;/h2&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		ajax技术对于整个web应用意义都是非凡的，仅以此篇致敬那些曾经奋斗在一线为了ajax技术的实现和普及做出工作的前辈们。\r\n	&lt;/p&gt;\r\n&lt;/p&gt;', '1484755200', '1', '转自&lt;掘金&gt;: @Damonare');
INSERT INTO `sw_news` VALUES ('7', 'Android 知识梳理', '本篇博客只是一个知识整理，因为网上对于Android的知识介绍足够多，因此我不再写相关文章（主要是因为我写的不如人家好），所以所有文章均来自网络，不贴原文章，只提供连接，因此本文旨在减少你对相关知识的搜索过程，方便查找相关知识。', '&lt;div style=&quot;font-weight:700;font-size:2em;padding:0.2em 0px;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	Android 知识梳理\r\n&lt;/div&gt;\r\n&lt;div style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;div&gt;\r\n		&lt;blockquote style=&quot;color:#666666;&quot;&gt;\r\n			&lt;p&gt;\r\n				说明：本篇博客只是一个知识整理，因为网上对于Android的知识介绍足够多，因此我不再写相关文章（主要是因为我写的不如人家好），所以所有文章均来自网络，不贴原文章，只提供连接，因此本文旨在减少你对相关知识的搜索过程，方便查找相关知识。\r\n			&lt;/p&gt;\r\n		&lt;/blockquote&gt;\r\n		&lt;h2&gt;\r\n			1.Activity\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Activity的生命周期，正常结束和非正常销毁的生命周期和调用函数及其使用方法；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-training-managing-the-activity-lifecycle-lesson-1/&quot; target=&quot;_blank&quot;&gt;Android Training - 详解Activity生命周期(Lesson 1 - 启动与销毁Activity)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-training-managing-the-activity-lifecycle-lesson-2/&quot; target=&quot;_blank&quot;&gt;Android Training - 详解Activity生命周期(Lesson 2 - 暂停与恢复activity)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-training-managing-the-activity-lifecycle-lesson-3/&quot; target=&quot;_blank&quot;&gt;Android Training - 详解Activity生命周期(Lesson 3 - 停止与重启activity)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-training-managing-the-activity-lifecycle-lesson-4/&quot; target=&quot;_blank&quot;&gt;Android Training - 详解Activity生命周期(Lesson 4 - 重新创建销毁的activity)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.cnblogs.com/hanyonglu/archive/2012/03/28/2420515.html&quot; target=&quot;_blank&quot;&gt;Android开发之InstanceState详解&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Activity启动模式\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://jcodecraeer.com/a/anzhuokaifa/androidkaifa/2015/0520/2897.html&quot; target=&quot;_blank&quot;&gt;Activity启动模式图文详解：standard, singleTop, singleTask 以及 singleInstance&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/zhangjg_blog/article/details/10923643&quot; target=&quot;_blank&quot;&gt;Android中Activity四种启动模式和taskAffinity属性详解&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Activity的启动过程\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6689748&quot; target=&quot;_blank&quot;&gt;Android应用程序启动过程源代码分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6703247&quot; target=&quot;_blank&quot;&gt;Android应用程序内部启动Activity过程（startActivity）的源代码分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6720261&quot; target=&quot;_blank&quot;&gt;Android应用程序在新的进程中启动新的Activity的方法和过程分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Activity的回收过程及原理；\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			2.View\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					View的绘制流程；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/wangjinyu501/article/details/9008271&quot; target=&quot;_blank&quot;&gt;Android View绘制流程&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://a.codekk.com/detail/Android/lightSky/%E5%85%AC%E5%85%B1%E6%8A%80%E6%9C%AF%E7%82%B9%E4%B9%8B%20View%20%E7%BB%98%E5%88%B6%E6%B5%81%E7%A8%8B&quot; target=&quot;_blank&quot;&gt;公共技术点之 View 绘制流程&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/qinjuning/article/details/8051811&quot; target=&quot;_blank&quot;&gt;Android中measure过程、WRAP_CONTENT详解以及xml布局文件解析流程浅析(上)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/qinjuning/article/details/8074262&quot; target=&quot;_blank&quot;&gt;Android中measure过程、WRAP_CONTENT详解以及xml布局文件解析流程浅析(下)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/qinjuning/article/details/7474827&quot; target=&quot;_blank&quot;&gt;Android中View(视图)绘制不同状态背景图片原理深入分析以及StateListDrawable使用详解&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/qinjuning/article/details/7226787&quot; target=&quot;_blank&quot;&gt;Android中将布局文件/View添加至窗口过程分析 ---- 从setContentView()谈起&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					View的事件分发原理；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.jianshu.com/p/e99b5e8bd67b#&quot; target=&quot;_blank&quot;&gt;图解 Android 事件分发机制&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/wangjinyu501/article/details/22584465&quot; target=&quot;_blank&quot;&gt;Android 中Touch（触屏）事件传递机制&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.cnblogs.com/sunzn/archive/2013/05/10/3064129.html&quot; target=&quot;_blank&quot;&gt;Android 编程下 Touch 事件的分发和消费机制&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/lvxiangan/article/details/9309927&quot; target=&quot;_blank&quot;&gt;Android-onInterceptTouchEvent()和onTouchEvent()总结&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					注：上面这篇文章没找到原创，连接是转载的，如果谁找到原创可以提供给我。\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/49203945&quot; target=&quot;_blank&quot;&gt;Android中View的量算、布局及绘图机制&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/49403315&quot; target=&quot;_blank&quot;&gt;源码解析Android中View的measure量算过程&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/50366021&quot; target=&quot;_blank&quot;&gt;源码解析Android中View的layout布局过程&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					自定义View；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/aigestudio/article/details/41212583&quot; target=&quot;_blank&quot;&gt;自定义控件其实很简单(系列教程)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.gcssloop.com/customview/CustomViewIndex&quot; target=&quot;_blank&quot;&gt;安卓自定义View教程目录&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					自定义ViewGroup；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/lmj623565791/article/details/38339817&quot; target=&quot;_blank&quot;&gt;Android 手把手教您自定义ViewGroup&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/lmj623565791/article/details/38352503&quot; target=&quot;_blank&quot;&gt;Android 自定义ViewGroup 实战篇 -&amp;gt; 实现FlowLayout&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/51314039&quot; target=&quot;_blank&quot;&gt;Android中自定义View、ViewGroup理论基础详解&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					ListView：\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/guolin_blog/article/details/44996879&quot; target=&quot;_blank&quot;&gt;Android ListView工作原理完全解析，带你从源码的角度彻底理解&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			3.Broadcast\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					广播的分类及区别,注册方法；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.cnblogs.com/net168/p/3980068.html&quot; target=&quot;_blank&quot;&gt;解析BroadcastReceiver之你需要了解的一些东东&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					广播实现跨进程通信的原理；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6737352&quot; target=&quot;_blank&quot;&gt;Android应用程序注册广播接收器（registerReceiver）的过程分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6744448&quot; target=&quot;_blank&quot;&gt;Android应用程序发送广播（sendBroadcast）的过程分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			4.Service\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Service的生命周期；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/48169339&quot; target=&quot;_blank&quot;&gt;Android中bindService的使用及Service生命周期&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					绑定和非绑定Service的使用方法；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/48169339&quot; target=&quot;_blank&quot;&gt;Android中bindService的使用及Service生命周期&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/48015475&quot; target=&quot;_blank&quot;&gt;Android通过startService实现批量下载示例&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Service的原理；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6745181&quot; target=&quot;_blank&quot;&gt;Android应用程序绑定服务（bindService）的过程源代码分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					IntentService:\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/48046861&quot; target=&quot;_blank&quot;&gt;Android中IntentService的使用及其源码解析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Accessibility Services:\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.android-doc.com/guide/topics/ui/accessibility/services.html&quot; target=&quot;_blank&quot;&gt;Building Accessibility Services(建立可访问性服务)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/androidsecurity/article/details/41890369?utm_source=tuicool&quot; target=&quot;_blank&quot;&gt;Android Accessibility(辅助功能) --实现Android应用自动安装、卸载&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.infoq.com/cn/articles/android-accessibility-installing?utm_campaign=infoq_content&amp;amp;utm_source=infoq&amp;amp;utm_medium=feed&amp;amp;utm_term=global&quot; target=&quot;_blank&quot;&gt;使用Android Accessibility实现免Root自动批量安装功能&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			5.ContentProvider\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					ContentProvider的原理及使用方法；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.sina.com.cn/s/blog_9f233c070101euqx.html&quot; target=&quot;_blank&quot;&gt;Android ContentProvider和Uri详解 (绝对全面)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6950440&quot; target=&quot;_blank&quot;&gt;Android应用程序组件Content Provider应用实例&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6963418&quot; target=&quot;_blank&quot;&gt;Android应用程序组件Content Provider的启动过程源代码分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6967204&quot; target=&quot;_blank&quot;&gt;Android应用程序组件Content Provider在应用程序之间共享数据的原理分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/luoshengyang/article/details/6985171&quot; target=&quot;_blank&quot;&gt;Android应用程序组件Content Provider的共享数据更新通知机制分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					启动过程，与Applocation和Activity比较启动顺序是怎样的；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					Contentprovider的onCreate方法在Application的onCreate方法前面，资料如下：\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.jianshu.com/p/a1f40b39b3de&quot; target=&quot;_blank&quot;&gt;android 应用的启动过程分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			6.Handler\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Handle的原理及机制；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.cnblogs.com/codingmyworld/archive/2011/09/12/2174255.html&quot; target=&quot;_blank&quot;&gt;android的消息处理机制（图+源码分析）——Looper,Handler,Message&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/lmj623565791/article/details/38377229&quot; target=&quot;_blank&quot;&gt;Android 异步消息处理机制 让你深入理解 Looper、Handler、Message三者关系&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/47180325&quot; target=&quot;_blank&quot;&gt;深入源码解析Android中的Handler,Message,MessageQueue,Looper&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Handler的发送处理消息的方法总会及各自的优缺点；\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Handler的正确使用方法；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://stackoverflow.com/questions/11278875/handlers-and-memory-leaks-in-android&quot; target=&quot;_blank&quot;&gt;Handlers and memory leaks in Android&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			7.Intent\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Intent的原理及使用方法；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/48417779&quot; target=&quot;_blank&quot;&gt;Android中Intent概述及使用&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/48481793&quot; target=&quot;_blank&quot;&gt;Android中Intent对象与Intent Filter过滤匹配过程详解&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/iispring/article/details/48578295&quot; target=&quot;_blank&quot;&gt;Android中常见Intent习惯用法-上篇(附源码下载)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Intent属性；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/github_25928675/article/details/46460417&quot; target=&quot;_blank&quot;&gt;Android权限和动作大全&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			8.网络\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Socket的原理及使用；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/jiajia4336/article/details/8798421&quot; target=&quot;_blank&quot;&gt;Socket通信原理和实践&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					TCP、UDP的原理；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.chinaunix.net/uid-26833883-id-3627644.html&quot; target=&quot;_blank&quot;&gt;TCP、UDP、IP 协议分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					Http通信原理；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;https://github.com/GeniusVJR/LearningNotes/blob/master/Part4/Network/Http%E5%8D%8F%E8%AE%AE.md&quot; target=&quot;_blank&quot;&gt;Http协议&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.cnblogs.com/rocomp/p/4803864.html&quot; target=&quot;_blank&quot;&gt;HTTP协议报文、工作原理及Java中的HTTP通信技术详解&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			9.Bluetooth\r\n		&lt;/h2&gt;\r\n		&lt;h3&gt;\r\n			1.蓝牙2.0\r\n		&lt;/h3&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				Bluetooth介绍及原理\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				Bluetooth通信方式\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				Bluetooth使用方法\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h3&gt;\r\n			2.蓝牙4.0\r\n		&lt;/h3&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				BLE介绍及原理\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				BLE通信方式\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					BLE的使用方法\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.jianshu.com/p/ffce0bbba84d&quot; target=&quot;_blank&quot;&gt;低功耗蓝牙BLE外围模式(peripheral)-使用BLE作为服务端&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			10.内存优化\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					系统GC回收过程及原理；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.importnew.com/13504.html&quot; target=&quot;_blank&quot;&gt;Java GC系列（1）：Java垃圾回收简介&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.importnew.com/13493.html&quot; target=&quot;_blank&quot;&gt;Java GC系列（2）：Java垃圾回收是如何工作的？&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.importnew.com/13827.html&quot; target=&quot;_blank&quot;&gt;Java GC系列（3）：垃圾回收器种类&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.importnew.com/13838.html&quot; target=&quot;_blank&quot;&gt;Java GC系列（4）：垃圾回收监视和分析&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					系统GC回收的触发条件；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.cnblogs.com/killmyday/archive/2013/06/12/3132518.html&quot; target=&quot;_blank&quot;&gt;Android内存管理原理&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.ibm.com/developerworks/cn/opensource/os-cn-android-mmry-rcycl/&quot; target=&quot;_blank&quot;&gt;Android 操作系统的内存回收机制&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					内存优化;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/a396901990/article/details/37914465&quot; target=&quot;_blank&quot;&gt;ANDROID内存优化(大汇总——上)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/a396901990/article/details/38707007&quot; target=&quot;_blank&quot;&gt;ANDROID内存优化(大汇总——中)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/a396901990/article/details/38904543&quot; target=&quot;_blank&quot;&gt;ANDROID内存优化(大汇总——全)&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-performance-memory/&quot; target=&quot;_blank&quot;&gt;Android性能优化之内存篇&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					UI优化；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.nimbledroid.com/2016/03/21/ways-to-hang-main-thread-zh.html?hmsr=toutiao.io&amp;amp;utm_medium=toutiao.io&amp;amp;utm_source=toutiao.io&quot; target=&quot;_blank&quot;&gt;5个导致主线程卡顿较鲜为人知的元凶&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/xyz_lmn/article/details/14524567&quot; target=&quot;_blank&quot;&gt;Android抽象布局——include、merge 、ViewStub&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.venmo.com/hf2t3h4x98p5e13z82pl8j66ngcmry/performance-tuning-on-android&quot; target=&quot;_blank&quot;&gt;Performance Tuning On Android&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					性能优化；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-performance-patterns/&quot; target=&quot;_blank&quot;&gt;Android性能优化典范（一）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-performance-patterns-season-2/&quot; target=&quot;_blank&quot;&gt;Android性能优化典范（二）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-performance-patterns-season-3/&quot; target=&quot;_blank&quot;&gt;Android性能优化典范（三）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-performance-patterns-season-4/&quot; target=&quot;_blank&quot;&gt;Android性能优化典范（四）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-performance-patterns-season-5/&quot; target=&quot;_blank&quot;&gt;Android性能优化典范（五）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;https://mp.weixin.qq.com/s?__biz=MzAxMzYyNDkyNA==&amp;amp;mid=403778409&amp;amp;idx=1&amp;amp;sn=2955f5209f2cb46c327167e9f558013c&amp;amp;scene=0&amp;amp;key=710a5d99946419d93bd87693b2fb201a979a3f06f49072f49e0e5dd05b91de2dbe204e56cbcd8c71cac94e931791f5f3&amp;amp;ascene=0&amp;amp;uin=ODU2NjQ0ODgx&amp;amp;devicetype=iMac+MacBookPro12%2C1+OSX+OSX+10.11.4+build(15E65&quot; target=&quot;_blank&quot;&gt;Android应用性能优化个人总结--图形优化&lt;/a&gt;)\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					内存泄露详解及总结；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://rayleeya.iteye.com/blog/1956059&quot; target=&quot;_blank&quot;&gt;Android 系统稳定性 - OOM（一）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://rayleeya.iteye.com/blog/1956638&quot; target=&quot;_blank&quot;&gt;Android 系统稳定性 - OOM（二）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://rayleeya.iteye.com/blog/1955652&quot; target=&quot;_blank&quot;&gt;Android 系统稳定性 - ANR（一）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://rayleeya.iteye.com/blog/1955657&quot; target=&quot;_blank&quot;&gt;Android 系统稳定性 - ANR（二）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://rayleeya.iteye.com/blog/1956056&quot; target=&quot;_blank&quot;&gt;Android 系统稳定性 - ANR（三）&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/android-performance-oom/&quot; target=&quot;_blank&quot;&gt;Android内存优化之OOM&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://yq.aliyun.com/articles/3009&quot; target=&quot;_blank&quot;&gt;Android 内存泄漏总结&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.360doc.com/content/14/0526/11/9462341_381066152.shtml&quot; target=&quot;_blank&quot;&gt;ANDROID 探究oom内幕&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					内存检测工具及使用方法；\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					BlockCanary\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.zhaiyifan.cn/2016/01/16/BlockCanaryTransparentPerformanceMonitor/&quot; target=&quot;_blank&quot;&gt;BlockCanary — 轻松找出Android App界面卡顿元凶&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					LeakCanary\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.jcodecraeer.com/a/anzhuokaifa/androidkaifa/2015/0509/2854.html&quot; target=&quot;_blank&quot;&gt;LeakCanary:检测所有的内存泄漏&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			11.传感器\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				加速度\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				磁力\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				方向\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				陀螺\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				光线感应\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				压力\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				温度\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				接近\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				重力\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				线性加速度\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				旋转矢量\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			12.网络期刊\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.codekk.com/open-source-project-analysis&quot; target=&quot;_blank&quot;&gt;codeKK&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://gank.io/&quot; target=&quot;_blank&quot;&gt;干货集中营&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.androidweekly.cn/&quot; target=&quot;_blank&quot;&gt;Android技术周报&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.devtf.cn/?tag=%E5%91%A8%E6%8A%A5&quot; target=&quot;_blank&quot;&gt;开发技术前线&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://gold.xitu.io/#/&quot; target=&quot;_blank&quot;&gt;掘金&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://toutiao.io/&quot; target=&quot;_blank&quot;&gt;开发者头条&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			13.技术社区\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.ibm.com/developerworks/cn/&quot; target=&quot;_blank&quot;&gt;IBM developerWorks&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://tech.meituan.com/&quot; target=&quot;_blank&quot;&gt;美团点评技术团队&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://club.alibabatech.org/&quot; target=&quot;_blank&quot;&gt;阿里技术沙龙&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://mogu.io/&quot; target=&quot;_blank&quot;&gt;蘑菇街技术博客&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://taobaofed.org/&quot; target=&quot;_blank&quot;&gt;淘宝前段团队&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://bugly.qq.com/bbs/portal.php&quot; target=&quot;_blank&quot;&gt;Bugly社区&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.alloyteam.com/&quot; target=&quot;_blank&quot;&gt;腾讯AllyTeam Blog&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.importnew.com/&quot; target=&quot;_blank&quot;&gt;ImportNew&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://android.jobbole.com/&quot; target=&quot;_blank&quot;&gt;伯乐在线&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			14.资源网站\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;https://android-arsenal.com/&quot; target=&quot;_blank&quot;&gt;Android Arsenal&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://itlanbao.com/&quot; target=&quot;_blank&quot;&gt;IT蓝豹&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://a.code4app.com/&quot; target=&quot;_blank&quot;&gt;Code4App&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.23code.com/&quot; target=&quot;_blank&quot;&gt;23Code&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.see-source.com/index.html&quot; target=&quot;_blank&quot;&gt;开源社区&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;https://www.coolgithubprojects.com/#&quot; target=&quot;_blank&quot;&gt;CoolGithubProjects&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://www.ctolib.com/android/&quot; target=&quot;_blank&quot;&gt;CTOLib&lt;/a&gt;\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			15.博客\r\n		&lt;/h2&gt;\r\n		&lt;ul&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://androidperformance.com/&quot; target=&quot;_blank&quot;&gt;Gracker&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					专注写Android优化的博客。\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://hukai.me/&quot; target=&quot;_blank&quot;&gt;胡凯&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					Android性能优化典范作者，高品质文章。\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/Luoshengyang?viewmode=contents&quot; target=&quot;_blank&quot;&gt;老罗的Android之旅&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					Android源码分析精品系列。\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				&lt;p&gt;\r\n					&lt;a href=&quot;http://blog.csdn.net/harvic880925&quot; target=&quot;_blank&quot;&gt;启舰&lt;/a&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					集合了动画、自定义控件以及Android各种知识的详解。\r\n				&lt;/p&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;h2&gt;\r\n			最后\r\n		&lt;/h2&gt;\r\n		&lt;p&gt;\r\n			同步发布地址：&lt;a href=&quot;http://www.codemx.cn/2016/05/04/2016-05-04-Android-Tree/&quot; target=&quot;_blank&quot;&gt;墨香博客&lt;/a&gt;\r\n		&lt;/p&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;', '1484582400', '0', '转自&lt;掘金&gt;: @墨香');
INSERT INTO `sw_news` VALUES ('8', '1月前端面试记', '1月前端面试记', '&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	背景\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	我于16.12.18辞职，之前有过一年左右的前端工作经验。从12月26号开始到1月9号先后面试了微信，百度，阿里巴巴uc，唯品会以及深圳腾讯等几家公司，特此总结与各位共勉。\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	微信\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	由于我已经毕业工作过，所以去微信面试是走的社招。微信社招极其严格，共八轮面试，总体来说我基本上当了一把炮灰。由于腾讯前端又细分为重构和JS，所以微信的面试基本上以JS为主。&lt;br /&gt;\r\n微信第一面是笔试，共一个小时，四道编程题:\r\n&lt;/p&gt;\r\n&lt;ul style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;li&gt;\r\n		JS手写二分搜索算法\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		给你一段代码，让你发现其中的问题(考察闭包)\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		实现一个Lazyman(请自行搜索)\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		用JS代码求出页面上一个元素的最终的background-color，不考虑IE浏览器，不考虑元素float情况。\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	前两道题比较基础，只要有JS基础的同学一般都应该没什么问题。第三道题，有些难度，但是我之前曾在网上见过该题，使用队列或者promise都可以。第四道题，看完之后一脸懵逼，想了半天也没明白它考察什么内容。由于时间关系，我直接用了window.getComputedStyle。后来交完之后仔细想想，好像确实有很多情况没考虑到，例如当这个元素的背景色为透明时，它最终的背景色应该为其父元素的背景色。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	微信二面是项目经历面，我跟面试官大致介绍下我过去一年做的项目。然后掏出笔记本开始一起看我之前写的代码，面试官觉得我之前做的项目复杂度不太高。然后就问了我其他几个问题:既然你面的是小程序组，那么你了解小程序吗？我说不了解。然后问我是否使用过React，我说没有。面试官不是很满意，但最终让我过了。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	微信第三面是前端基础面，面完之后我的人生观发生了改变，我感觉我不配做前端。第一个问题是，请问前端优化的手段有哪些？我心里暗喜，这么简单的问题还问。然后我答将CSS文件放在上面，JS文件放在下面。正准备说下一条时，面试官问为什么这样做?我答JS下载解析时会阻塞DOM树的构建，如果放在上面可能会出现白屏的情况。然后面试官问，有没有可能让JS下载解析不阻塞DOM树构建?我答使用defer或asyn字段。面试官问有什么区别？我答...然后面试官问CSS下载解析会不会阻塞DOM树渲染?我已经开始一身汗了，这个问题我不太确定。然后面试官让我接着说优化手段，我答CSS-sprit或者将小图使用base64内嵌。面试官问我CSS-sprit原理是什么?使用base64虽然会减少请求数，但是会增大文件的大小，以什么为基准去衡量什么时候使用base64?此时我已经泪流满面。然后面试官问我了解HTTPS吗？我说了解。他问，请问HTTPS和HTTP有什么区别?我答HTTPS增加了SSL层。面试官问请画出SSL四次握手过程?此刻已经崩溃，我答不会。然后他问请问SSL握手时有对称加密和非对称加密吗？我答不知道。他问如何优化这一层？我答不知道。然后继续回答优化手段?我答将静态内容推向CDN。然后他问现在CDN不稳，时而工作时而崩溃，我有一个首页如何确保每次都能正常显示该首页?答案是CSS inline。他看了看时间，说那就下一个问题。然后给了我一个柱状图，让我使用html和CSS绘制出来。当时我是用了flex。然后他让我手写一个快排算法，我写了。然后他让我用CSS和JS动画在刚才那个柱状图中表现出快排的整个过程，然后我哭了。其实这个动画是见过的，只不过平时使用的都是canvas。面试结束，我跪了。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	整体来言，微信的面试难度还是挺大的。他对每个细节的考察非常仔细，不仅要求你知其然更要让你知其为什么。另外一方面我也意识到了准备的不足。\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n	百度\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	百度的面试是我最喜欢的，考察的比较全面。百度第一面为基础面，包括HTML/CSS/JS各方面，共50分钟左右，大约30个问题左右:\r\n&lt;/p&gt;\r\n&lt;ul style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;li&gt;\r\n		HTML5新特性(新增的标签, API等)，如localstorage的用法以及与cookie的区别，如何理解web语义化\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		CSS3新特性，如动画等\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		CSS特性，如position的用法，如何实现居中，bootstrap源代码的理解，盒模型(W3C和IE)，flex的使用\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		前端兼容性处理(CSS hack技术)\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		JS基础，如this用法，new关键字的过程，call与apply的区别，闭包，原型以及JS如何实现继承\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		前端基础，浏览器缓存，跨域，从输入url到渲染的整个过程，事件(W3C和IE)，TCP三次握手过程，如何实现懒加载(跟预加载的区别)\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	百度二面为基础面+算法+项目经历，持续一个小时。问题如下:\r\n&lt;/p&gt;\r\n&lt;ul style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;li&gt;\r\n		之前有看过你做的一个移动页简历，请问如何实现？我主要是使用REM+Media Query，根据不同尺寸的设备进行不同的font-size设置。然后问我REM和EM的区别，如果父元素的font-size也是采用em表示，那么子元素的font-size怎么计算等。\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		有没有遇到过margin重叠的现象，如何解决？BFC\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		常见的清除浮动的方法有哪些？bootstrap是怎么做的？bootstrap是怎么实现grid系统的？\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		怎么理解JS模块化？有没有使用过webpack？\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	算法题只有一道:什么是浅复制和深复制？有什么区别？如何实现Object的深复制？\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n	首先我对这个问题进行分析，Object是一个树形结构，所以我采用递归的方法进行复制。面试官随后提问能否通过循环的方法？我思考了一会，回答说循环的关键在于循环条件的设置，我想借助栈作为循环判断的条件，当栈为空时，循环结束。当时我立马反应过来，因为Object子节点的个数不确定，可能入栈出栈会存在一定问题。面试官随后问，还有什么东西没有考虑到吗？我想了下说没有。他提示了下，如果出现环怎么办？我愣了一下说不知道。然后他给我个提示说使用深度优先的方法借助栈并不能解决这个问题，然后让我使用宽度优先试试，将代码发给他。面试结束后我发给了他我的代码，各位可以参考下:\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n&lt;pre class=&quot;prettyprint lang-js&quot;&gt;// 深度优先遍历复制, 借助队列\r\nfunction deepCopy(obj) {\r\n    var newObj = {},\r\n        srcQueue = [obj], srcVisitedQueue = [],\r\n        copyQueue = [newObj], copyVisitedQueue = [];\r\n\r\n    while (srcQueue.length &amp;gt; 0) {\r\n        var currentSrcElement = srcQueue.shift(),\r\n            currentCopyElement = copyQueue.shift();\r\n\r\n        srcVisitedQueue.push(currentSrcElement);\r\n        copyVisitedQueue.push(currentCopyElement);\r\n\r\n        for (var key in currentSrcElement) {\r\n            if (typeof currentCopyElement[key] !== \'object\') {\r\n                currentCopyElement[key] = currentSrcElement[key];\r\n            } else {\r\n                // 有环的情况\r\n                var index = srcVisitedQueue.indexOf(currentSrcElement[key]);\r\n                if (index &amp;gt;= 0) {\r\n                    currentCopyElement[key] = copyVisitedQueue[index];\r\n                } else {\r\n                    srcQueue.push(currentSrcElement[key]);\r\n                    currentCopyElement[key] = {};\r\n                    copyQueue.push(currentCopyElement[key]);\r\n                }\r\n            }\r\n        }\r\n    }\r\n\r\n    return newObj;\r\n}\r\n\r\n// Test case\r\n// 1. 只含有简单类型的Object{a: 1, b:2} =&amp;gt; pass\r\n// 2. 简单类型和复杂类型同时存在的情况下的Object =&amp;gt; pass:\r\n// var obj1 = {\r\n//     a: 1,\r\n//     b: 2,\r\n//     c: {\r\n//         d: 3,\r\n//         e: {\r\n//             f: 4,\r\n//             g: 5\r\n//         }\r\n//     },\r\n//     h: {\r\n//         i: 6,\r\n//         j: 7\r\n//     }\r\n// };\r\n// 3. 有环的情况下的Object =&amp;gt; pass:\r\n// var obj1 = {\r\n//     a: 1,\r\n//     b: 2,\r\n//     c: obj1\r\n// };&lt;/pre&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		然后面试官问了关于我项目经历，之前曾看到你推进过公司的模块化，请问是基于什么背景，你是如何推进的，遇到过什么问题等等。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		百度三面就是属于开放性面试。首先面试官跟我讨论了下我二面当中的那道算法题，问我是否解决，环如何解决，然后问是否看过Jquery的源代码怎么进行Object的深复制的？我答看过，是采用递归。他然后问为什么Jquery对环的考虑只判断当前节点是否为根节点?该问题我没回答上来。然后他说现在百度有许多部门，每个部门都有自己的组件库，实现的功能基本相同，但有的用的是原生的JS，有的是使用JQuery，有的是使用React，现在想进行统一，如何解决？我说采用重构，他问了问我重构的思路有哪些。然后他说百度有些项目在线上已经很久了，代码比较陈旧，如果你去重构可能会对线上的版本有冲击，你怎么解决这个问题?我答小幅度重构，然后进行回滚等。然后他问，现在有一个很急的项目，需要一个组件，刚好网上提供的有该组件，你是如何屏蔽该组件与现有项目的差异?我的思路是写一个wrapper屏蔽掉该组件与现有库的差异。然后面试官问，如果引入该组件必须引入一个新的库，如React你该如何选择？引入的话有什么好处坏处？我提供了自己的一些看法。\r\n	&lt;/p&gt;\r\n	&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n		阿里巴巴UC\r\n	&lt;/h3&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		UC的面试是最轻松的，以至于我感觉我可能没通过。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		UC第一面是一个阿里非常高级的前端工程师，面试大约一小时。首先让我讲了讲我对移动前端的了解？然后我讲了讲移动前端布局，JS方面，性能优化等，大于15分钟。然后他问我如何理解前端工程化？然后我又讲了15分钟等。然后他问我有什么问题吗？我问请问你如何理解前端以及你是如何从pc端转入移动端？他站在黑板上给我讲了20多分钟。包括hybrid技术以及现在流行的RN和Weex，以及阿里现在的业务使用的一些技术以及为什么使用等。第一轮面试结束。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		第二轮面试是一个后端的哥们，面试大约30分钟。问了我一些前端优化的方法，以及平常遇到过最大的困难是什么，如何解决。有什么难忘的事等。然后问我有什么问题？我说为什么你不问我前端的基础知识也不问我前端的项目经验？他说没必要，已经了解我的背景了等等，然后带我吃了个饭。\r\n	&lt;/p&gt;\r\n	&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n		唯品会\r\n	&lt;/h3&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		唯品会技术面共5轮，考察的非常全面。前两面都是基础面跟百度大概类似。但是它重点问了我web安全的一些知识，xss和csrf。问我如何解决相关的问题？xss的话主要使用转义，csrf的话项目中我主要使用的是jwt(javascript web token)。然后针对jwt细节问了我20多分钟。第三面是一个产品经理面，说虽然是技术但是还是要了解产品才行。问我有没有用过唯品会app，我说没有。然后问我知道唯品会是干什么的吗？我说一家专做特卖的网站。然后给我一个唯品会的页面问我有什么建议，然后我讲了20多分钟。第四面是技术总监，进来之后先把我吊了一顿。说虽然感觉你前端基础各方面比较好，但是唯品会的业务性非常强，我没有任何电商方面的经验等，让我过来从头做起之类的。\r\n	&lt;/p&gt;\r\n	&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n		腾讯\r\n	&lt;/h3&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		由于面试的是移动方面的业务，所以主要问的有zepto库的使用，移动端布局，优化，JS的一些基础还有localstorage等。重点还问了一下我是如何在项目中开展测试的(单元测试和e2e测试)。后序面试由于要去深圳总部，所以我推掉了。\r\n	&lt;/p&gt;\r\n	&lt;h3 style=&quot;color:#333333;font-family:-apple-system, &amp;quot;background-color:#FFFFFF;&quot;&gt;\r\n		总结\r\n	&lt;/h3&gt;\r\n	&lt;p style=&quot;color:#333333;font-family:-apple-system, &amp;quot;font-size:medium;background-color:#FFFFFF;&quot;&gt;\r\n		针对于前端的面试，还是需要一定的时间去好好梳理，尤其是知识点的细节一定要非常清楚。关于项目经历一定要有一条主线，在这条主线上你做过什么，遇到过什么坑，怎么解决一定要非常清楚等。另外一点算法还是很重要的，推荐大家一本书&amp;lt;&amp;lt;剑指offer&amp;gt;&amp;gt;，面试前我前前后后看过三遍。共勉之！\r\n	&lt;/p&gt;\r\n&lt;/p&gt;', '1484668800', '0', '转自&lt;掘金&gt;: @WU_CHONG');
INSERT INTO `sw_news` VALUES ('9', '2016年，我对爬虫的总结', '都说年末了，该给自己写写总结了。今天我想谈一谈的是我在公司这一年多里的负责的部分工作---爬虫。做了这么久的爬虫，是该写点什么，留下点什么。在我所负责的这一段时间了。我总结了一下大概有以下几类爬虫设计思想。', '&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	都说年末了，该给自己写写总结了。今天我想谈一谈的是我在公司这一年多里的负责的部分工作---爬虫。做了这么久的爬虫，是该写点什么，留下点什么。在我所负责的这一段时间了。我总结了一下大概有以下几类爬虫设计思想。\r\n&lt;/p&gt;\r\n&lt;ul style=&quot;margin-left:22px;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;li&gt;\r\n		简单服务器定时爬虫\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		客户端爬虫\r\n		&lt;ul style=&quot;margin-left:22px;&quot;&gt;\r\n			&lt;li&gt;\r\n				lua解析\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				javascript解析\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		服务器离线爬虫\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	下面在来细聊以下。\r\n&lt;/p&gt;\r\n&lt;h2 style=&quot;font-family:-apple-system, &amp;quot;color:#2F2F2F;font-size:24px;background-color:#FFFFFF;&quot;&gt;\r\n	服务器-定时简单爬虫\r\n&lt;/h2&gt;\r\n&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	在最开始的时候，我们做的就是这个。这应该是最简单的爬虫了。搜索引擎搜出来的大概就是这一类了。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	这一类的爬虫特点是我只需要爬取一个网站的某一部分数据，发起http请求做html解析，然后存数据库，就完了。比如一些其他网站提供的一些公共数据，或者不要求实时性的数据。如汽车之家的汽车数据，如英雄联盟的英雄数据，如政府网站的某些展示数据。我用过&lt;a href=&quot;https://scrapy.org/&quot; target=&quot;_blank&quot;&gt;scrapy&lt;/a&gt;来抓取dota2的一些数据，后来发现scrapy不一定完全在自己掌控中，我自己写了一个爬虫框架，叫&lt;a href=&quot;https://github.com/hirudy/Tspider&quot; target=&quot;_blank&quot;&gt;Tspider&lt;/a&gt;。我现在做一些简单的爬虫都会用它。基于php的协程与curl_multi_*函数集实现的。单进程可到2000有效处理/min。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	这一类的爬虫处理流程大概类似如下\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;img src=&quot;http://upload-images.jianshu.io/upload_images/301894-5efc3e2d426faa5a.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240&quot; alt=&quot;&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		网上宣传的诸如《xxx抓取淘宝MM照片》、《我从知乎偷取了多少数据》等等爬虫，大概如此。《21天精通xx语言》的既视感。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		这一类的爬虫的优点大概就是简单吧。\r\n	&lt;/p&gt;\r\n	&lt;h2 style=&quot;font-family:-apple-system, &amp;quot;color:#2F2F2F;font-size:24px;background-color:#FFFFFF;&quot;&gt;\r\n		客户端解析（lua、javascript）\r\n	&lt;/h2&gt;\r\n	&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		我们公司是做移动端产品的，假如你也恰好是有（android/ios）客户端支持的。并且对数据的实时性要求较高，或者爬数据的时候ip封的太厉害了。可以试试我下面说的方式。我给他取名叫客户端解析。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		在这一类爬虫中，需要客户端嵌入脚本执行引擎。把http的请求与数据解析放到客户端去执行。最后把数据呈现出来或者上报给服务器。准确度、实时性较高。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img src=&quot;http://upload-images.jianshu.io/upload_images/301894-cd5cb7bd57087e36.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240&quot; alt=&quot;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;ul style=&quot;margin-left:22px;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n			&lt;li&gt;\r\n				脚本：脚本的作用不过是把对应网站的数据（json、jsonp，html等等）转换为我们需要的数据（格式化数据）。当对方做出改变，我们只需要改变脚本就行。\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				策略：策略的作用是告诉客户端，你们需要某部分时候，去执行脚本中的哪个方法？ 是否需要缓冲？是否需要展示原始网站的内容？等等一些通过服务器控制客户端行为的东西。\r\n			&lt;/li&gt;\r\n			&lt;li&gt;\r\n				离线爬虫：是否需要在服务器爬取，将请求丢给消息队列，离线爬虫系统自己一边玩去。\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n		&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n			这种方式至少有两个优点：ip离散，实时性较高。\r\n		&lt;/p&gt;\r\n		&lt;h2 style=&quot;font-family:-apple-system, &amp;quot;color:#2F2F2F;font-size:24px;background-color:#FFFFFF;&quot;&gt;\r\n			服务器-离线爬虫系统\r\n		&lt;/h2&gt;\r\n		&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n			对于绝大部分服务，数据的爬取需求，还是需要在服务器完成的。对于这一类的爬虫。架构的设计，需要有很好的扩展性。\r\n		&lt;/p&gt;\r\n&lt;img src=&quot;http://upload-images.jianshu.io/upload_images/301894-5bcd05889ad219b7.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240&quot; alt=&quot;&quot; /&gt;\r\n		&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n			&lt;br /&gt;\r\n		&lt;/p&gt;\r\n		&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n			&lt;ul style=&quot;margin-left:22px;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n				&lt;li&gt;\r\n					爬取请求从后台控制、对外网关而来。\r\n				&lt;/li&gt;\r\n				&lt;li&gt;\r\n					爬虫触发器，就是通过消息队列告诉爬虫节点啥时候爬，爬谁的数据。\r\n				&lt;/li&gt;\r\n				&lt;li&gt;\r\n					后台控制，可以控制支持哪些网站的数据，告警，异常管理。\r\n				&lt;/li&gt;\r\n				&lt;li&gt;\r\n					消息队列用于分发消息给某个爬虫节点。\r\n				&lt;/li&gt;\r\n				&lt;li&gt;\r\n					爬虫节点：完成具体的爬虫，格式化爬虫数据。支持某个网站的爬取，通常修改此次就行了，这里也需要做好统计，做好告警。\r\n				&lt;/li&gt;\r\n				&lt;li&gt;\r\n					去重：去重可以试试布隆过滤器与simhash指纹算法和海明距离比较。\r\n				&lt;/li&gt;\r\n			&lt;/ul&gt;\r\n			&lt;h2 style=&quot;font-family:-apple-system, &amp;quot;color:#2F2F2F;font-size:24px;background-color:#FFFFFF;&quot;&gt;\r\n				思想或叫准则吧\r\n			&lt;/h2&gt;\r\n			&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n				总结一下：\r\n			&lt;/p&gt;\r\n			&lt;ul style=&quot;margin-left:22px;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n				&lt;li&gt;\r\n					只关心对的，不关心错的。错误的，你永远也枚举不完。\r\n				&lt;/li&gt;\r\n				&lt;li&gt;\r\n					在分层结构中，应该是越往最内层，数据(请求)越小，有效数据(请求)占比越高。\r\n				&lt;/li&gt;\r\n				&lt;li&gt;\r\n					备份思想很重要。如果一台机器挂断的概率是百分之一，两台机器同时挂掉的概率就是万分之一。\r\n				&lt;/li&gt;\r\n				&lt;li&gt;\r\n					没有什么万能钥匙，具体问题，具体分析，具体解决。\r\n				&lt;/li&gt;\r\n				&lt;li&gt;\r\n					没有什么完美的解决方案，有些时候需要根据业务做一些取舍。\r\n				&lt;/li&gt;\r\n			&lt;/ul&gt;\r\n			&lt;p style=&quot;color:#2F2F2F;font-family:-apple-system, &amp;quot;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n				最后，感谢我的leader——corey，谢谢！\r\n			&lt;/p&gt;\r\n		&lt;/p&gt;\r\n	&lt;/p&gt;\r\n&lt;/p&gt;', '1484491250', '1', '转自&lt;掘金&gt;: @林湾村龙猫');

-- ----------------------------
-- Table structure for sw_role
-- ----------------------------
DROP TABLE IF EXISTS `sw_role`;
CREATE TABLE `sw_role` (
  `role_id` smallint(4) unsigned NOT NULL AUTO_INCREMENT,
  `role_name` varchar(20) NOT NULL COMMENT '角色名称',
  `role_desc` varchar(255) NOT NULL DEFAULT '' COMMENT '角色描述',
  `role_auth_list` varchar(50) NOT NULL DEFAULT '' COMMENT '角色权限列表:ids 1,2,5',
  PRIMARY KEY (`role_id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of sw_role
-- ----------------------------
