INSERT INTO `sw_role` VALUES ('3', '打杂的', '只拥有对内容管理等的权限.', '1,5,6,3,10,11,12,13,18,19');

-- ----------------------------
-- Table structure for sw_setting
-- ----------------------------
DROP TABLE IF EXISTS `sw_setting`;
CREATE TABLE `sw_setting` (
  `set_id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `set_name` varchar(30) NOT NULL DEFAULT '',
  `set_logo` varchar(50) NOT NULL DEFAULT '' COMMENT '网站LOGO',
  `set_domain` varchar(50) NOT NULL COMMENT '网站域名',
  `set_keywords` text COMMENT '网站关键字',
  `set_desc` text COMMENT '网站描述',
  `set_contacts` varchar(30) NOT NULL DEFAULT '' COMMENT '联系人',
  `set_phone` varchar(20) NOT NULL DEFAULT '',
  `set_tel` varchar(20) NOT NULL DEFAULT '',
  `set_qq` varchar(15) NOT NULL DEFAULT '',
  `set_email` varchar(20) NOT NULL DEFAULT '',
  `set_addr` varchar(100) NOT NULL DEFAULT '',
  `set_footer` text NOT NULL COMMENT '底部信息',
  `set_content` text,
  PRIMARY KEY (`set_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of sw_setting
-- ----------------------------
INSERT INTO `sw_setting` VALUES ('1', '春招集训营', '/Public/Uploads/Setting/20170121/588358c437adb.png', 'pwwtest.com', '春招，课程。', '春招集训营是一家非营利性的机构，旨在帮助在秋招中未得到offer或者想寻求更好的机会的人在春招中获得理想的offer。 这里有大神帮你认清职业方向，指导正确的学习方法，让你少走弯路，更高效的Coding。', '饼哥', '183***9614', '051-6078955', '695933122', 'zqmath@126.com', '北京市朝阳区', 'Copyright © 2017. pww版权所有', '&lt;h3&gt;\r\n	春招集训营汇聚了各类大神。&lt;br /&gt;\r\n这里有拿到百度、华为、搜狐等大型互联网公司的大牛，也有知名的ACM亚洲银牌选手。&lt;br /&gt;\r\n他们能够将平时所学的知识，遇到的坑，细致的讲解，让你少走弯路。\r\n&lt;/h3&gt;');
SET FOREIGN_KEY_CHECKS=1;
